  methods: {
    getImgUrl(image_path) {
      let path = "";
      if (/jpeg$/.test(image_path)) {
        path = "jpeg";
      } else if (/png$/.test(image_path)) {
        path = "png";
      }
      return `//fuss10.elemecdn.com/${image_path}.${path}`;
